import express from 'express'
import bodyParser from 'Body-parser'
import {MongoClient} from 'mongodb'



const app = express();

app.use(bodyParser.json());

app.get('/api/articles/:name', async (req, res) => {
    try {
        const articleName = req.params.name;

            //"mongodb+srv://cluster0.ntfef.mongodb.net/myFirstDatabase"
    const myclient = await MongoClient.connect("mongodb+srv://ThorStenbaek:Mushroomstar2409@cluster0.ntfef.mongodb.net/myFirstDatabase"
, { native_parser: true })
    
    const db = myclient.db('LinkedIn')
    
    const articlesInfo = await db.collection('LinkedInLearning').findOne({ name: articleName })
    res.status(200).json(articlesInfo);

    myclient.close();
    }
    catch (error) {
        res.status(500).json({message: "something went wrong.", error})
    }
    
})

app.post('/api/articles/:name/upvote', (req, res) => {
    const articleName = req.params.name; 

    articlesInfo[articleName].upvotes += 1;
    res.status(200).send(`${articleName} now has ${articlesInfo[articleName].upvotes} upvotes!`)
})

app.post('/api/articles/:name/add-comment', (req, res) => { 
    const { username, text } = req.body; 
    const articleName = req.params.name; 

    articlesInfo[articleName].comments.push({ username, text })
    res.status(200).send(articlesInfo[articleName]);
})

app.listen(8000, () => console.log("Listening on port 8000"))
